mapper.select:
select a.id,u.username as author,a.title,a.description,a.cover,a.create_time,LEFT(a.content,40) as content from article a,user u
,article_tag at
a.user_id = u.id and a.type = 1 and a.status = 1
and a.user_id = #{userId}
and at.article_id = a.id and at.tag_id = #{tag}
order by a.create_time desc

mapper.delete:
delete from article where id in

    #{id}

and type = #{type}

mapper.select:
select a.id,a.content,a.author,a.title,a.description,a.cover,a.create_time from article a where a.type = 1 and a.status = 1 and a.id in
#{id}
order by a.create_time desc

